var hotelSearch=document.querySelector(".hotel-search-button");var popup=document.querySelector(".modal-search");var dateArrive=popup.querySelector("[name=date-arrive]");var form=popup.querySelector(".hotel-search-form");var dateLeave=popup.querySelector("[name=date-leave]");var adultsAmount=popup.querySelector("[name=adults-number]")
hotelSearch.addEventListener("click",function(evt){evt.preventDefault();popup.classList.toggle("modal-search-hide");dateArrive.focus();popup.classList.remove("modal-error");});form.addEventListener("submit",function(evt){if(!dateArrive.value||!dateLeave.value||!adultsAmount.value){evt.preventDefault();popup.classList.add("modal-error");if(!dateArrive.value){dateArrive.focus();}
if(!dateLeave.value){dateLeave.focus();}
if(!adultsAmount.value){adultsAmount.focus();}}});